(279, 'widget_revfoods_realfoodsforschool', 'a:2:{i:2;a:6:{s:5:"title";s:20:"real food in schools";s:11:"add_submenu";s:2:"on";s:5:"image";i:67;s:7:"content";s:310:"To us, the math is easy. Each week, we serve over one million freshly prepared, healthy meals to schools across the country. That''s a million instances of more productive, more attentive, better motivated and better performing students. We like making math easy. And history. And art. And english. And science.";s:4:"link";s:46:"http://wpvalet.cyahost.com/revfoods/?page_id=7";s:15:"learn_more_text";s:41:"learn more about our school meals program";}s:12:"_multiwidget";i:1;}', 'yes'),
(280, 'widget_revfoods_realfoodsforstore', 'a:2:{i:2;a:9:{s:5:"title";s:19:"real food in stores";s:11:"add_submenu";s:2:"on";s:5:"image";i:68;s:8:"headline";s:17:"healthy meal kits";s:18:"headline_font_size";s:4:"70px";s:19:"headline_font_color";s:4:"#fff";s:7:"content";s:148:"Our story began in schools. Now, we''re bringing our recipe for success to your home with our all natural Meal Kits, perfect for on-the-go nutrition.";s:4:"link";s:46:"http://wpvalet.cyahost.com/revfoods/?page_id=8";s:15:"learn_more_text";s:36:"learn more about our retail products";}s:12:"_multiwidget";i:1;}', 'yes'),
